import React, {useState} from 'react'

const [showUpdateForm, setShowUpdateForm] = useState(false)
const [updatedLocation, setUpdatedLocation] = useState()
const [updatedCity, setUpdatedCity] = useState()
const [updatedZip, setUpdatedZip] = useState()
const [updatedDescription, setUpdatedDescription] = useState()
const [updatedImage, setUpdatedImage] = useState()
const [updatedRating, setUpdatedRating] = useState()
const [updatedTags, setUpdatedTags] = useState()

const DisplayCards = (props) => {
    return (
        <>
            <div className="col-12 col-sm-6 col-md-6 col-lg-4 col-xl-3">
                <div className="card p-5" style={{minWidth: '350px'}}>
                    <img className="card-img" src={props.location.image} alt="..."/>
                    <div className='card-body'>
                        <h5 className='card-title'>{props.location.location}</h5>
                        <p className='card-text'>{props.location.city}</p>
                        <p className='card-text'>{props.location.rating} Stars</p>
                    </div>
                    <button onClick={ (event) => {props.handleDelete(props.location)}}>Delete Location</button>
                    {props.showUpdateForm ?
                                    <form onSubmit={(event) => {props.handleUpdateForm(props.location)}}>
                                        location: <input type="text" defaultValue={props.location.location} onChange={props.handleLocationUpdate}/><br/>
                                        city: <input type="text" defaultValue={props.location.city} onChange={props.handleCityUpdate}/><br/>
                                        zip: <input type="number" defaultValue={props.location.zip} onChange={props.handleZipUpdate}/><br/>
                                        description: <input type="text" defaultValue={props.location.description} onChange={props.handleDescriptionUpdate}/><br/>
                                        image: <input type="text" defaultValue={props.location.image} onChange={props.handleImageUpdate}/><br/>
                                        rating: <input type="number" defaultValue={props.location.rating} onChange={props.handleRatingUpdate}/><br/>
                                        tags: <input type="text" defaultValue={props.location.tags} onChange={props.handleTagsUpdate}/><br/>
                                        <input type="submit" value="Update Location"/>
                                    </form>
                                :
                                    <button onClick={props.showForm}>Edit</button>
                                }
                </div>
            </div>
        </>
    )
}

export default DisplayCards
