import './App.css';
import {useState, useEffect} from 'react'
import axios from 'axios'

function App() {

    const [locations, setLocations] = useState([])


    const handleNewLocationChange = (event) => {
        setNewLocation(event.target.value)
    }

    const handleNewCityChange = (event) => {
        setNewCity(event.target.value)
    }

    const handleNewZipChange = (event) => {
        setNewZip(event.target.value)
    }

    const handleNewDescriptionChange = (event) => {
        setNewDescription(event.target.value)
    }

    const handleNewImageChange = (event) => {
        setNewImage(event.target.value)
    }

    const handleNewRatingChange = (event) => {
        setNewRating(event.target.value)
    }

    const handleNewTagsChange = (event) => {
        setNewTags(event.target.value)
    }

    // POST ROUTE
    const handleNewLocationFormSubmit = (event) => {
        event.preventDefault()
        axios.post(
            'https://project3-travelapp-backend.herokuapp.com/locations',
            {
                location: newLocation,
                city: newCity,
                zip: newZip,
                description: newDescription,
                image: newImage,
                rating: newRating,
                tags: newTags,
            }
        ).then(() => {
            axios   
                .get('https://project3-travelapp-backend.herokuapp.com/locations')
                .then((response) => {
                    setLocations(response.data)
                })
        })
        event.target.reset()
    }

    // GET ROUTE
    useEffect(() => {
        axios
            .get('https://project3-travelapp-backend.herokuapp.com/locations')
            .then((response) => {
                setLocations(response.data)
                console.log(response.data)
            })
    })

    // DELETE ROUTE
    const handleDelete = (locationData) => {
        axios
            .delete(`https://project3-travelapp-backend.herokuapp.com/locations/${locationData._id}`)
            .then(() => {
                axios
                    .get('https://project3-travelapp-backend.herokuapp.com/locations')
                    .then((response) => {
                        setLocations(response.data)
                    })
                })
    }


    return (
        <>
        </>
    )
}

export default App;
